mapper.select:
SELECT ID , USER_ID , NUMBER , AMOUNT , STATUS , TRANSFER_LIMIT , DAILY_TRANSFER_LIMIT , CREATED_AT , UPDATED_AT FROM ACCOUNTS WHERE 1 = 1
AND NUMBER = #{number}
AND ID = #{id}
AND 1 = 2

mapper.insert:
INSERT INTO ACCOUNTS
(
    USER_ID
    , NUMBER
    , AMOUNT
    , STATUS
    , TRANSFER_LIMIT
    , DAILY_TRANSFER_LIMIT
    , CREATED_AT
    , UPDATED_AT
) VALUES (
    #{userId}
    , #{number}
    , #{amount}
    , #{status}
    , #{transferLimit}
    , #{dailyTransferLimit}
    , CURRENT_TIMESTAMP
    , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE ACCOUNTS
SET STATUS = #{status}
    , UPDATED_AT = CURRENT_TIMESTAMP
WHERE 1 = 1
    AND ID = #{id}
